import './App.css';
import Header from '../Components/Header/header';
import SectionEditor from '../Components/Sections/Editor/Section-Editor';
import Footer from '../Components/Footer/footer';
import ButtonTheme from '../Components/Theme/button-theme';
import UserSection from '../Components/Sections/User/User-Section';
import LabelAddSection from '../Components/Sections/Labels/Label-Logo-Add';
import { useState } from 'react';


export const sectionIds = {
  "sectionEditor": "SectionEditor",
  "userSection": "UserSection",
  "labelAddSection": "LabelAddSection",
}

const sectionComponent = {
  "SectionEditor": <div>{sectionIds.sectionEditor}</div>,
  "UserSection": <div>{sectionIds.userSection}</div>,
  "LabelAddSection": <div>{sectionIds.labelAddSection}</div>,
}

const Button = ({ isHighlighted, onClick }) => {
  return <div styles={{ background: isHighlighted ? "blue" : "grey" }} onClick={() => onSectionButtonClick(sectionIds.sectionEditor)}>Редактор Шаблонов</div>
}

function CustomControlSection({ onSectionButtonClick }) {
  return (
    <div id="custom-control-button">
      <div className="button-control-section" id="button-section-editor" onClick={() => onSectionButtonClick(sectionIds.sectionEditor)}>Редактор Шаблонов</div>
      <div className="button-control-section" id="button-section-user-editor" onClick={() => onSectionButtonClick(sectionIds.userSection)}>Редактор Пользователей</div>
      <div className="button-control-section" id="button-section-logo-editor" onClick={() => onSectionButtonClick(sectionIds.labelAddSection)}>Редактор Данных</div>
    </div>
  );
};



function App() {

  const [openedSectionId, setOpenedSectionId] = useState(sectionIds.sectionEditor)

  return (
    <div className="App">
      <Header />
      <CustomControlSection onSectionButtonClick={(sectionId) => setOpenedSectionId(sectionId)} />
      {sectionComponent[openedSectionId]}
      {/* <SectionEditor />
      <UserSection />
      <LabelAddSection /> */}
      <Footer />
      <ButtonTheme />
    </div>
  );
};

export default App;
