import LeftSectionEditor from './Left-Section/Left-Section-Editor';
import RightSectionEditor from './Right-Section/Right-Section-Editor';
import './style-editor.css';
import { resetSelection } from '../../function-move-elem';
import Error from '../../Alert-Errors/Error';
import AlertPrint from '../../Alert-Print/Alert-Print';

export let liHistory;                                                                              //? Для списка шаблонов
export let selectHistory = document.querySelector('.select-box-history');                          //? Получение селекта шаблонов
export let soValue = document.getElementById('soValue');                                           //? Получение инпута списка шаблонов
export let optionSearch = document.getElementById('optionSearch');                                 //? 
export let ulHistory = document.querySelector('.option-history');                                  //? Обработчик на блоке списка бирки

function SectionEditor() {
    //* Функция получения элемента списка на который нажали
    ulHistory.onclick = async function (event) {
        liHistory = event.target.closest('li');
        await inputTextHistory(liHistory);
    };
    //* Функция поиска имени бирки
    optionSearch.addEventListener('keyup', function () {
        let filter, li, i, textValue;
        filter = optionSearch.value.toUpperCase();
        li = ulHistory.getElementsByTagName('li');
        for (i = 0; i < li.length; i++) {
            let liCount = li[i];
            textValue = liCount.textContent || liCount.innerText;
            if (textValue.toUpperCase().indexOf(filter) > -1) {
                li[i].style.display = '';
            } else {
                li[i].style.display = 'none';
            };
        };
    });

    return (
        <div id="section">
            <div id="section-left">
                <h2 id="header-left" onClick={resetSelection}>Позиционирование данных</h2>
                <LeftSectionEditor />
            </div>
            <div id="section-right">
                <h2 id="haeder-right" onClick={resetSelection}>Бирка</h2>
                <RightSectionEditor />
            </div>
            <AlertPrint />
            <Error />
        </div>
    );
};

export default SectionEditor;
